function initSmoothScroll(){document.querySelectorAll(".table-of-contents a").forEach(t=>{t.addEventListener("click",function(t){t.preventDefault();var t=this.getAttribute("href").slice(1),t=document.getElementById(t);t&&(t=t.getBoundingClientRect().top+window.pageYOffset-100,window.scrollTo({top:t,behavior:"smooth"}))})})}document.addEventListener("DOMContentLoaded",initSmoothScroll);